BEGIN TRANSACTION;
CREATE TABLE libs (
	id INTEGER NOT NULL,
	user_id INTEGER,
	readme TEXT,
	lib TEXT,
	title VARCHAR(128),
	description TEXT,
	version VARCHAR(32),
	license VARCHAR(128),
	tags TEXT,
    date DATETIME,
    shasum VARCHAR(40),
	PRIMARY KEY (id)
);
CREATE TABLE `reference` (
	`id`	INTEGER,
	`parent_id`	INTEGER,
	`term`	VARCHAR(32),
	`text`	TEXT,
	PRIMARY KEY(`id`)
);
CREATE TABLE users (
	id INTEGER NOT NULL,
	name VARCHAR(128),
    username VARCHAR(128),
	email VARCHAR(128),
	password VARCHAR(128),
	date_created DATETIME,
	date_login DATETIME,
	login_token VARCHAR(128),
    reset_token VARCHAR(128),
    date_reset DATETIME,
    reset_id VARCHAR(128),
	PRIMARY KEY (id)
);
COMMIT;
